import React, { useState } from "react";
import "../CSS/Group.css";
import LogoAndTitle from "../Components/LogoAndTitle";
import { group } from "console";
import "../Components/groupList";

interface GroupInfo {
  groupName: string;
}
export const GroupList: React.FC = () => {
  const [group, setGroup] = useState(new Array<GroupInfo>());

  const addGroup = (groupName: string) => {
    const g = group.slice();
    g.push({ groupName });
    setGroup(g);
  };

  const removeGroup = (i: number) => {
    const g = group.slice();
    g.splice(i, 1);
    setGroup(g);
  };
  const handleSubmit = (evt: any) => {
    evt.preventDefault();
  };
  return (
    <>
      <div>
        <h1>Group Name</h1>
        <header className="Group-Header">
          <LogoAndTitle />
        </header>
      </div>
      <div>
        {" "}
        <button className="Add Group"> Add Group</button>
      </div>
    </>
  );
};
type GroupType = {
  name: string;
};
function ViewEvents(props: {
  Groups: any;
  DeleteEvents: any;
  showEditEvent: any;
}) {
  return (
    <div>
      {props.Groups.map((group: GroupType, id: number) => (
        <tr>
          <th>
            <button onClick={() => props.DeleteEvents(id)}>
              {" "}
              Delete Group{" "}
            </button>
          </th>
          <th>
            <button onClick={() => props.showEditEvent(id)}>
              {" "}
              Edit Event{" "}
            </button>
          </th>
          <th> {group.name} </th>
        </tr>
      ))}
    </div>
  );
}

type MyProps = {
  // using `interface` is also ok
  groupName: string;
};

export default class GroupPage extends React.Component<
  MyProps,
  { groups: any; edit: boolean; curEditId: number }
> {
  constructor(public props: MyProps) {
    super(props);
    this.state = {
      groups: [
        {
          name: "Good Group",
        },
        {
          name: "Bad Group",
        },
      ],
      edit: false,
      curEditId: -1,
    };
  }
  getNewEvent(name: string) {
    this.state.groups.push({ name });
    this.setState({ groups: this.state.groups });
  }
}
